public long getVirtualMemorySize(int olderThanAge) {
  long total = UNAVAILABLE;
  for (ProcessInfo p : processTree.values()) {
    if ((p != null) && (p.getAge() > olderThanAge)) {
      if (total == UNAVAILABLE ) {
        total = 0;
      }
      total += p.getVmem();
    }
  }
  return total;
}
