private static Integer getValidPID(String pid) {
  Integer retPid = -1;
  try {
    retPid = Integer.parseInt(pid);
    if (retPid <= 0) {
      retPid = -1;
    }
  } catch (NumberFormatException nfe) {
    retPid = -1;
  }
  return retPid;
}
