public static String addSuffix(String key, String suffix) {
  if (suffix == null || suffix.isEmpty()) {
    return key;
  }
  if (suffix.startsWith(".")) {
    throw new IllegalArgumentException("suffix '" + suffix + "' should not " +
        "already have '.' prepended.");
  }
  return key + "." + suffix;
}
