public InetSocketAddress updateConnectAddr(String name,
                                           InetSocketAddress addr) {
  String prefix = name;
  if (HAUtil.isHAEnabled(this)) {
    prefix = HAUtil.addSuffix(prefix, HAUtil.getRMHAId(this));
  }
  return super.updateConnectAddr(prefix, addr);
}
