public static int readAll(BlockReader reader,
    byte[] buf, int offset, int len) throws IOException {
  int n = 0;
  for (;;) {
    int nread = reader.read(buf, offset + n, len - n);
    if (nread <= 0)
      return (n == 0) ? nread : n;
    n += nread;
    if (n >= len)
      return n;
  }
}

