public static void readFully(BlockReader reader,
    byte[] buf, int off, int len) throws IOException {
  int toRead = len;
  while (toRead > 0) {
    int ret = reader.read(buf, off, toRead);
    if (ret < 0) {
      throw new IOException("Premature EOF from inputStream");
    }
    toRead -= ret;
    off += ret;
  }
}

