public void subtractFrom(int pmem, int vmem, float cpu) {
  this.setPhysicalMemory(this.getPhysicalMemory() - pmem);
  this.setVirtualMemory(this.getVirtualMemory() - vmem);
  this.setCPU(this.getCPU() - cpu);
}
