public Date getDate() {
  Object date = getInfo().get(DATE_INFO_KEY);
  if (date != null) {
    if (date instanceof Long) {
      return new Date((Long)date);
    } else if (date instanceof Date) {
      return (Date)date;
    }
  }
  return null;
}

