public Path toPath() throws URISyntaxException {
  String scheme = getScheme() == null ? "" : getScheme();

  String authority = "";
  if (getHost() != null) {
    authority = getHost();
    if (getUserInfo() != null) {
      authority = getUserInfo() + "@" + authority;
    }
    if (getPort() > 0) {
      authority += ":" + getPort();
    }
  }

  return new Path(
      (new URI(scheme, authority, getFile(), null, null)).normalize());
}
