private void stringify(StringBuilder sb) {
  sb.append(getMessage())
      .append("\n")
      .append(getRemoteTrace());
  final SerializedException cause = getCause();
  if (cause != null) {
    sb.append("Caused by: ");
    cause.stringify(sb);
  }
}
