private static void checkUnitArgument(String unit) {
  if (unit == null) {
    throw new IllegalArgumentException("Unit cannot be null");
  } else if (!KNOWN_UNITS.contains(unit)) {
    throw new IllegalArgumentException("Unknown unit '" + unit + "'");
  }
}
