static void validateWriteArgs(byte[] b, int off, int len)
    throws IOException {
  Preconditions.checkNotNull(b);
  if ((off < 0) || (off > b.length) || (len < 0) ||
      ((off + len) > b.length) || ((off + len) < 0)) {
    throw new IndexOutOfBoundsException(
        "write (b[" + b.length + "], " + off + ", " + len + ')');
  }
}
