public static String resource(Path path,
    final boolean isDirectory,
    boolean addWildcard) {
  String key = pathToKey(path);
  if (isDirectory && !key.isEmpty()) {
    key = key + "/";
  }
  return resource(path.toUri().getHost(), key, addWildcard);
}
