public static String[] directory(Path path) {
  String host = path.toUri().getHost();
  String key = pathToKey(path);
  if (!key.isEmpty()) {
    return new String[] {
        resource(host, key + "/", true),
        resource(host, key, false),
        resource(host, key + "/", false),
    };
  } else {
    return new String[]{
        resource(host, key, true),
    };
  }
}
