public static Statement statement(
    final boolean allow,
    final Path path,
    final boolean isDirectory,
    final boolean wildcards,
    final String... actions) {
  return new Statement(RoleModel.effect(allow))
      .addActions(actions)
      .addResources(resource(path, isDirectory, wildcards));
}
