void merge(boolean isClosed) {
  if (mergedStats != null) {
    mergeInputStreamStatistics(diff(mergedStats));
  } else {
    mergeInputStreamStatistics(this);
  }
  // If stats are closed, no need to create another copy
  if (!isClosed) {
    mergedStats = copy();
  }
}
