public static long validateReadahead(@Nullable Long readahead) {
  if (readahead == null) {
    return Constants.DEFAULT_READAHEAD_RANGE;
  } else {
    Preconditions.checkArgument(readahead >= 0, E_NEGATIVE_READAHEAD_VALUE);
    return readahead;
  }
}
