private static String passwordDiagnostics(String pass, String description) {
  if (pass == null) {
    return "null " + description;
  }
  int len = pass.length();
  switch (len) {
  case 0:
    return "empty " + description;
  case 1:
    return description + " of length 1";

  default:
    return description + " of length " + len + " ending with "
        + pass.charAt(len - 1);
  }
}
