public static void closeAutocloseables(Logger log,
    AutoCloseable... closeables) {
  if (log == null) {
    log = LOG;
  }
  for (AutoCloseable c : closeables) {
    if (c != null) {
      try {
        log.debug("Closing {}", c);
        c.close();
      } catch (Exception e) {
        log.debug("Exception in closing {}", c, e);
      }
    }
  }
}

