public void metastoreOperationRetried(Exception ex,
    int retries,
    boolean idempotent) {
  operationRetried(ex);
  incrementStatistic(S3GUARD_METADATASTORE_RETRY);
  if (isThrottleException(ex)) {
    incrementStatistic(S3GUARD_METADATASTORE_THROTTLED);
