private void incrementBytesRead(long bytesRead) {
  if (bytesRead > 0) {
    pos.addAndGet(bytesRead);
  }
  streamStatistics.bytesRead(bytesRead);
  if (readContext.getStats() != null && bytesRead > 0) {
    readContext.getStats().incrementBytesRead(bytesRead);
  }
}
