public static S3AFileSystem verifyIsS3AFS(FileSystem fs, Path path)
    throws PathCommitException {
  if (!(fs instanceof S3AFileSystem)) {
    throw new PathCommitException(path, E_WRONG_FS);
  }
  return (S3AFileSystem) fs;
}
