public void setUri(String fsURI) {
  if (fsURI != null) {
    if (!fsURI.endsWith("/")) {
      fsURI += "/";
    }
    try {
      setUri(new URI(fsURI));
    } catch (URISyntaxException e) {
      throw new RuntimeException(e);
    }
  }
}
