private static void throwExceptionOnVersionMismatch(int actual,
    String tableName,
    String exMsg) throws IOException {

  if (VERSION != actual) {
    throw new IOException(exMsg + " Table " + tableName
        + " Expected version: " + VERSION + " actual tag version: " +
        actual);
  }
}
