void shutdown() {
  // stop the dump thread
  if (streamMonitor != null && streamMonitor.isAlive()) {
    streamMonitor.shouldRun(false);
    streamMonitor.interrupt();
    try {
      streamMonitor.join(3000);
    } catch (InterruptedException e) {
    }
  }

  cleanAll();
}
