public static <T> void ignoreIOExceptions(
    Logger log,
    String action,
    String path,
    Operation<T> operation) {
  try {
    once(action, path, operation);
  } catch (IOException e) {
    log.info("{}: {}", toDescription(action, path), e.toString(), e);
  }
}
