public void onFailure(String text,
    IOException exception,
    int retries,
    boolean idempotent) {
  LOG.debug("{}: " + exception, text);
  if (retries == 1) {
    // stack on first attempt, to keep noise down
    LOG.debug("{}: " + exception, text, exception);
  }
}
