public final void run() {
  try {
    if (nextRun == startTime) {
      firstStep();
      nextRun += repeatInterval;
      if (nextRun <= endTime) {
        queue.add(this);          
      }
    } else if (nextRun < endTime) {
      middleStep();
      nextRun += repeatInterval;
      queue.add(this);
    } else {
      lastStep();
    }
  } catch (YarnException e) {
    e.printStackTrace();
  } catch (IOException e) {
    e.printStackTrace();
  } catch (InterruptedException e) {
    e.printStackTrace();
  }
}
