public static int getWeighted(Collection<Double> weights, Random rr) {

  double totalWeight = 0;
  for (Double i : weights) {
    totalWeight += i;
  }

  double rand = rr.nextDouble() * totalWeight;

  double cur = 0;
  int ind = 0;
  for (Double i : weights) {
    cur += i;
    if (cur > rand) {
      break;
    }
    ind++;
  }

  return ind;
}
