private void printUsage(Options opts, boolean printDetailed) {
  HelpFormatter helpFormatter = new HelpFormatter();
  if (printDetailed) {
    helpFormatter.printHelp(usage.length() + 10, usage, null, opts, null,
        false);
  } else {
    System.out.println(usage);
  }
}
