private static String getNonEmptyVal(Configuration conf, String key) {
  String value = conf.get(key);
  if (StringUtils.isEmpty(value)) {
    throw new IllegalArgumentException(
        "No value for " + key + " found in conf file.");
  }
  return value;
}
