private synchronized void cleanup() {
  try {
    if (metaFolder == null) return;

    jobFS.delete(metaFolder, true);
    metaFolder = null;
  } catch (IOException e) {
    LOG.error("Unable to cleanup meta folder: " + metaFolder, e);
  }
}
