private static void seekIfRequired(ThrottledInputStream inStream,
    long sourceOffset) throws IOException {
  try {
    if (sourceOffset != inStream.getPos()) {
      inStream.seek(sourceOffset);
    }
  } catch (IOException e) {
    throw new CopyReadException(e);
  }
}
