private boolean isFileNotFoundException(IOException e) {
  if (e instanceof FileNotFoundException) {
    return true;
  }

  if (e instanceof RemoteException) {
    return ((RemoteException)e).unwrapRemoteException()
        instanceof FileNotFoundException;
  }

  return false;
}
