boolean isDirectoryOrAncestorDeleted(Path dir) {
  if (dir == null) {
    // at root
    return false;
  } else if (isContained(dir)) {
    // cache hit
    return true;
  } else {
    // cache miss, check parent
    return isDirectoryOrAncestorDeleted(dir.getParent());
  }
}
