public void put(WorkRequest<T> workRequest) {
  boolean isDone = false;
  while (!isDone) {
    try {
      inputQueue.put(workRequest);
      workCnt.incrementAndGet();
      isDone = true;
    } catch (InterruptedException ie) {
      LOG.error("Could not put workRequest into inputQueue. Retrying...");
    }
  }
}
