public static long getFileSize(Path path, Configuration configuration)
                                          throws IOException {
  if (LOG.isDebugEnabled())
    LOG.debug("Retrieving file size for: " + path);
  return path.getFileSystem(configuration).getFileStatus(path).getLen();
}
