public synchronized int read() throws IOException {
  verifyOpen();
  int result = -1;
  try {
    result = httpStream.read();
  } catch (IOException e) {
    String msg = "IOException while reading " + path
                 + ": " +e + ", attempting to reopen.";
    LOG.debug(msg, e);
    if (reopenBuffer()) {
      result = httpStream.read();
    }
  }
  if (result != -1) {
    incPos(1);
  }
  if (statistics != null && result != -1) {
    statistics.incrementBytesRead(1);
  }
  return result;
}
