private int chompBytes(long bytes) throws IOException {
  int count = 0;
  if (httpStream != null) {
    int result;
    for (long i = 0; i < bytes; i++) {
      result = httpStream.read();
      if (result < 0) {
        throw new SwiftException("Received error code while chomping input");
      }
      count ++;
      incPos(1);
    }
  }
  return count;
}
