private static String encodeUrl(String url) throws SwiftException {
  if (url.matches(".*\\s+.*")) {
    try {
      url = URLEncoder.encode(url, "UTF-8");
      url = url.replace("+", "%20");
    } catch (UnsupportedEncodingException e) {
      throw new SwiftException("failed to encode URI", e);
    }
  }

  return url;
}
