public static byte[] toAsciiByteArray(String s) {
  char[] chars = s.toCharArray();
  int len = chars.length;
  byte[] buffer = new byte[len];
  for (int i = 0; i < len; i++) {
    buffer[i] = (byte) (chars[i] & 0xff);
  }
  return buffer;
}
