public static byte[] writeTextFile(FileSystem fs,
                                 Path path,
                                 String text,
                                 boolean overwrite) throws IOException {
  FSDataOutputStream stream = fs.create(path, overwrite);
  byte[] bytes = new byte[0];
  if (text != null) {
    bytes = toAsciiByteArray(text);
    stream.write(bytes);
  }
  stream.close();
  return bytes;
}
