public static void assertPathExists(FileSystem fileSystem, String message,
                             Path path) throws IOException {
  if (!fileSystem.exists(path)) {
    //failure, report it
    fail(message + ": not found " + path + " in " + path.getParent());
         ls(fileSystem, path.getParent());
  }
}
