public static String joinPaths(String path1, String path2) {
  StringBuilder result =
          new StringBuilder(path1.length() + path2.length() + 1);
  result.append(path1);
  boolean insertSlash = true;
  if (path1.endsWith("/")) {
    insertSlash = false;
  } else if (path2.startsWith("/")) {
    insertSlash = false;
  }
  if (insertSlash) {
    result.append("/");
  }
  result.append(path2);
  return result.toString();
}
