public boolean isDescendantOfSnapshotRoot(INodeDirectory snapshotRootDir) {
  Preconditions.checkArgument(snapshotRootDir.isSnapshottable());
  INodeDirectory dir = this;
  while(dir != null) {
    if (dir.equals(snapshotRootDir)) {
      return true;
    }
    dir = dir.getParent();
  }
  return false;
}
