public boolean addChildAtLoading(INode node) {
  int pos;
  if (!node.isReference()) {
    pos = (children == null) ? (-1) : (-children.size() - 1);
    addChild(node, pos);
    return true;
  } else {
    return addChild(node);
  }
}
