private void addChild(final INode node, final int insertionPoint) {
  if (children == null) {
    children = new ArrayList<>(DEFAULT_FILES_PER_DIRECTORY);
  }
  node.setParent(this);
  children.add(-insertionPoint - 1, node);

  if (node.getGroupName() == null) {
    node.setGroup(getGroupName());
  }
}
