void updateReplicationFactor(Collection<UpdatedReplicationInfo> blocks) {
  BlockManager bm = getBlockManager();
  for (UpdatedReplicationInfo e : blocks) {
    BlockInfo b = e.block();
    bm.setReplication(b.getReplication(), e.targetReplication(), b);
  }
}
