private Throwable getRootCause(Throwable throwable) {
  if (throwable == null) {
    throw new IllegalArgumentException("throwable can not be null");
  }

  Throwable result = throwable;
  while (result.getCause() != null) {
    result = result.getCause();
  }

  return result;
}
