private void appendIfNotEmpty(StringBuilder sb, String regEx,
    boolean shouldAppendSemiColon) {
  if (regEx == null || regEx.trim().isEmpty()) {
    return;
  }
  sb.append(SINGLE_WHITE_SPACE);
  sb.append(regEx);
  if (shouldAppendSemiColon) {
    sb.append(SEMICOLON);
  }
}
